function Filmstrip(c, b, f) {
    $(c).jcarousel({
        start: f,
        scroll: 1,
        auto: 0,
        animation: 900,
        easing: "easeInOutQuint",
        wrap: "none",
        itemFallbackDimension: 940,
        initCallback: function (a) {
            if (a.options.size > 2) {
                $(a.get(1)).click(function () {
                    a.scroll(2)
                });
                for (var d = 1; d <= a.options.size - 1; d++) $(b + " #item-nav").append('<li><a href="javascript:void(0)" rel="' + d + '">' + d + "</a></li>");
                $(b + " #item-nav li a").click(function () {
                    $(this).removeClass("normal").addClass("selected");
                    a.scroll(parseInt($(this).attr("rel")))
                });
                $(b + " #prev").click(function () {
                    a.prev();
                    _gaq.push(["_trackEvent", "Filmstrip-Event", "Select-Previous", c])
                });
                $(b + " #next").click(function () {
                    a.next();
                    _gaq.push(["_trackEvent", "Filmstrip-Event", "Select-Next", c]);
                    
                    
                });
                $(c).swipe({
                    swipeLeft: function () {
                        a.next();
                        _gaq.push(["_trackEvent", "Filmstrip-Swipe", "Left", c])
                    },
                    swipeRight: function () {
                        a.prev();
                        _gaq.push(["_trackEvent", "Filmstrip-Swipe", "Right", c])
                    },
                    triggerOnTouchEnd: !0,
                    allowPageScroll: "auto"
                })
            } else $(b).hide();
            $(a.get(a.options.size)).fadeTo(0, 0)
        },
        itemFirstInCallback: {
            onBeforeAnimation: function (a) {
                for (var b = 2; b < a.options.size; b++) {
            	    $(a.get(b)).children().fadeTo(500, 0.45);
              
                }
            },
            onAfterAnimation: function (a, c, e) {
                $(b + " #item-nav li a").removeClass("selected").addClass("normal");
                $(b + " #item-nav li a").eq(e - 1).removeClass("normal").addClass("selected");
                e <= 1 ? $(b + " #prev").fadeTo(0, 0.5).unbind("click").css("cursor", "pointer") : $(b + " #prev").fadeTo(0, 1).bind("click", function () {
                    a.prev()
                }).css("cursor", "pointer");
                e >= a.options.size - 1 ? $(b + " #next").fadeTo(0, 0.5).unbind("click").css("cursor", "pointer") : $(b + " #next").fadeTo(0,
                1).bind("click", function () {
                    a.next()
                }).css("cursor", "pointer");
                $(a.get(e)).children().fadeTo(500, 1)
            }
        },
        buttonNextHTML: null,
        buttonPrevHTML: null
    })
};